function onMouseClick(){for(let e=0;e<document.querySelectorAll(".drum").length;e++)document.querySelectorAll("button")[e].addEventListener("click",function(){let n=document.querySelectorAll("button")[e].innerHTML;callSound(n),buttonAnimation(n)})}function onKeyBoardClick(){addEventListener("keydown",function(e){callSound(e.key),buttonAnimation(e.key)})}function callSound(e){switch(e){case"j":(audio=new Audio("sounds/crash.mp3")).play();break;case"k":(audio=new Audio("sounds/kick-bass.mp3")).play();break;case"l":(audio=new Audio("sounds/snare.mp3")).play();break;case"w":(audio=new Audio("sounds/tom-1.mp3")).play();break;case"a":(audio=new Audio("sounds/tom-2.mp3")).play();break;case"s":(audio=new Audio("sounds/tom-3.mp3")).play();break;case"d":(audio=new Audio("sounds/tom-4.mp3")).play()}}function buttonAnimation(e){let n=document.querySelector("."+e);n.classList.add("pressed"),setTimeout(function(){n.classList.remove("pressed")},100)}onMouseClick(),onKeyBoardClick();